```c
#include<stdio.h>
int main()
{
    //右移:有符号数右移，符号位不变，高位补符号位
    int a2 = -1073741824;//int范围：-21亿多~21亿多
    int b2 = a2 >> 3;
    printf("b2:%d\n",b2);

    unsigned int a3 = 3373741824;//无符号数右移，高位补0, unsigned int 范围0~42亿多
    unsigned b3 = a3 >> 3;
    printf("b3:%u\n",b3);   
    return 0;
